SELECT og.id,og.shop_id,og.company_id,og.goods_sn,og.name,og.category_id,og.gallery, og.keywords,og.brief,og.is_on_sale,og.sort_order,og.pic_url,og.detail,og.createtime,og.id, og.modifytime,og.dr,ogs.id AS oid,ogs.goods_id,ogs.specification,ogs.specification,ogs.value, ogs.pic_url AS pic,ogs.modifier,ogs.creator,ogs.createtime,ogs.modifytime,ogs.dr FROM order_goods og LEFT JOIN order_goods_specification ogs ON og.id = ogs.goods_id WHERE og.shop_id = #{shopId}
AND og.name LIKE CONCAT('%',#{name,jdbcType=VARCHAR},'%')
AND og.dr = 0 AND ogs.dr = 0 AND og.is_on_sale = 1

SELECT og.id,og.shop_id,og.company_id,og.goods_sn,og.name,og.category_id,og.gallery, og.keywords,og.brief,og.is_on_sale,og.sort_order,og.pic_url,og.detail,og.createtime,og.id, og.modifytime,og.dr,ogs.id AS oid,ogs.goods_id,ogs.specification,ogs.specification,ogs.value, ogs.pic_url AS pic,ogs.modifier,ogs.creator,ogs.createtime,ogs.modifytime,ogs.dr FROM order_goods og LEFT JOIN order_goods_specification ogs ON og.id = ogs.goods_id WHERE og.shop_id = #{shopId}
AND og.name LIKE CONCAT('%',#{name,jdbcType=VARCHAR},'%')
AND og.category_id = #{categoryId} AND og.dr = 0 AND ogs.dr = 0 AND og.is_on_sale = 1
